use std::collections::HashSet;

pub fn solve(input: Vec<char>, size: usize) -> usize {
    let mut i = 0;
    while i + 4 < input.len() {
        let set: HashSet<&char> = HashSet::from_iter(input[i..i + size].iter());
        if set.len() == size {
            break;
        }
        i += 1;
    }

    i + size
}

#[cfg(test)]
mod tests {
    use super::*;

    const SAMPLE_INPUT: &str = "mjqjpqmgbljsphdztnvjfqwrcgsmlb";
    const INPUT: &str =
"";

    #[test]
    fn test_sample_part_a() {
        let result = solve(SAMPLE_INPUT.chars().collect(), 4);
        assert_eq!(result, 7);
    }

    #[test]
    fn test_part_a() {
        let result = solve(INPUT.chars().collect(), 4);
        assert_eq!(result, 1876);
    }

    #[test]
    fn test_sample_part_b() {
        let result = solve(SAMPLE_INPUT.chars().collect(), 14);
        assert_eq!(result, 19);
    }

    #[test]
    fn test_part_b() {
        let result = solve(INPUT.chars().collect(), 14);
        assert_eq!(result, 2202);
    }
}
